t.script:
                    document.addEventListener('DOMContentLoaded', function() {
                        const searchBtn = document.getElementById('searchContainersBtn');
                        const submitBtn = document.getElementById('submitBtn');
                        const matchingSection = document.getElementById('matchingContainersSection');
                        const matchingList = document.getElementById('matchingContainersList');

                        searchBtn.addEventListener('click', function() {
                            const fileName = document.getElementById('search_file_name').value;
                            const dateFrom = document.getElementById('search_date_from').value;
                            const dateTo = document.getElementById('search_date_to').value;
                            const alphaRange = document.getElementById('search_alpha_range').value;

                            if (!fileName) {
                                alert('Please enter the file name you\'re looking for');
                                return;
                            }

                            // Show loading
                            searchBtn.disabled = true;
                            searchBtn.innerHTML = ' Searching...';
                            matchingList.innerHTML = '
';
                            matchingSection.style.display = 'block';

                            // Make AJAX call
                            fetch('/my/request/search_containers', {
                                method: 'POST',
                                headers: {
                                    'Content-Type': 'application/json',
                                },
                                body: JSON.stringify({
                                    jsonrpc: '2.0',
                                    method: 'call',
                                    params: {
                                        file_name: fileName,
                                        date_from: dateFrom,
                                        date_to: dateTo,
                                        alpha_range: alphaRange
                                    }
                                })
                            })
                            .then(response => response.json())
                            .then(data => {
                                searchBtn.disabled = false;
                                searchBtn.innerHTML = ' Find Matching Boxes';

                                if (data.result && data.result.success) {
                                    const containers = data.result.containers;

                                    if (containers.length === 0) {
                                        matchingList.innerHTML = `



                                                No matching boxes found. We'll search through all your boxes.

                                        `;
                                        submitBtn.disabled = false;
                                    } else {
                                        let html = '
';
                                        html += `






                                                        Select All ${containers.length} Match${containers.length > 1 ? 'es' : ''}



                                        `;

                                        containers.forEach(function(container) {
                                            html += `






                                                            ${container.name} 
                                                            ${container.number ? '(' + container.number + ')' : ''}
                                                            Score: ${container.score}



                                                        ${container.alpha_range ? 'Range: ' + container.alpha_range + ' | ' : ''}
                                                        ${container.date_range ? 'Dates: ' + container.date_range + ' | ' : ''}
                                                        ${container.location ? 'Location: ' + container.location : ''}

                                                    ${container.reasons ? ' ' + container.reasons + '' : ''}
                                                    ${container.contents ? 'Contents: ' + container.contents + '' : ''}

                                            `;
                                        });
                                        html += '';

                                        matchingList.innerHTML = html;

                                        // Enable submit when at least one is checked
                                        const checkboxes = document.querySelectorAll('.container-checkbox');
                                        const selectAll = document.getElementById('selectAllContainers');

                                        selectAll.addEventListener('change', function() {
                                            checkboxes.forEach(cb => cb.checked = selectAll.checked);
                                            updateSubmitButton();
                                        });

                                        checkboxes.forEach(cb => {
                                            cb.addEventListener('change', updateSubmitButton);
                                        });

                                        function updateSubmitButton() {
                                            const anyChecked = Array.from(checkboxes).some(cb => cb.checked);
                                            submitBtn.disabled = !anyChecked;
                                        }
                                    }
                                } else {
                                    matchingList.innerHTML = `



                                            Search failed: ${data.result ? data.result.error : 'Unknown error'}

                                    `;
                                }
                            })
                            .catch(error => {
                                searchBtn.disabled = false;
                                searchBtn.innerHTML = ' Find Matching Boxes';
                                matchingList.innerHTML = `



                                        Search failed: ${error.message}

                                `;
                            });
                        });
                    });
